import { useState } from "react";
import "./App.css";
import Banner from "./components/Banner";
import QuickAccess from "./components/QuickAcess";
import SideNav from "./components/SideNav";

function App() {
  const [currentBalance, setCurrentBalance] = useState<number>(2000.5);
  const [incomeBalance, setIncomeBalance] = useState<number>(2000.5);
  const [debtBalance, setDebtBalance] = useState<number>(2000.5);

  return (
    <div className="App">
      <SideNav />
      <main className="ml-20 py-5">
        <div className="CurrentBalance text-left">
          <h2 className="text-lg text-gray-500">Current Balance</h2>
          <span className="font-bold text-4xl">${currentBalance.toFixed(2)}</span>
        </div>

        <QuickAccess />

        <Banner text="Planning a trip? Start a personalized SavingPlan now" />

        <div className="ThisMonth mt-10">
          <h2 className="font-bold text-2xl text-left">This month</h2>
          <div className="flex flex-row">
            <div className="flex flex-col text-left">
              <div>
                <h3>INCOME</h3>
                <span>{incomeBalance}</span>
              </div>
              <div>
                <h3>DEBT</h3>
                <span>{debtBalance}</span>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
