import { createContext, useState } from "react";
import { api } from "../api/services/Api";
import authService from "../api/services/AuthService";
import { User } from "../models/User";
import jwt_decode from "jwt-decode";
interface AuthContextData {
  user: User | null;
  setLoggedUser: any;
  SignIn(email: string, password: string): void;
  SignOut(): void;
  isAuthenticated: boolean;
}

interface Props {
  children: React.ReactNode;
}

interface AuthTokens {
  access: string;
  refresh: string;
}

const AuthContext = createContext<AuthContextData>({} as AuthContextData);
export const AuthProvider = ({ children }: Props) => {
  let [authTokens, setAuthTokens] = useState<AuthTokens | null>(null);
  let [loggedUser, setLoggedUser] = useState<User | null>(null);

  async function SignIn(email: string, password: string) {
    const response = (await authService.signIn(email, password)) as AuthTokens;
    setAuthTokens(response);
    setLoggedUser(jwt_decode(response.access));
  }

  async function SignOut() {
    setLoggedUser(null);
  }

  return (
    <AuthContext.Provider
      value={{
        user: loggedUser,
        setLoggedUser,
        isAuthenticated: Boolean(loggedUser),
        SignIn,
        SignOut,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
export default AuthContext;
