import { useState } from "react";
import "./App.css";
import Banner from "./components/Banner";
import CurrentMonthChart from "./components/charts/CurrentMonthChart";
import QuickAccess from "./components/QuickAcess";
import SideNav from "./components/SideNav";

function App() {
  const [currentBalance, setCurrentBalance] = useState<number>(2000.5);
  const [incomeBalance, setIncomeBalance] = useState<number>(1000.2);
  const [debtBalance, setDebtBalance] = useState<number>(2000.5);

  return (
    <div className="App">
      <SideNav />
      <main className="ml-20 py-5">
        <div className="CurrentBalance text-left">
          <h2 className="text-lg text-gray-500">Current Balance</h2>
          <span className="font-bold text-4xl">
            ${currentBalance.toFixed(2)}
          </span>
        </div>

        <QuickAccess />

        <Banner text="Planning a trip? Start a personalized SavingPlan now" />

        <div className="ThisMonth mt-10 mr-6">
          <h2 className="font-bold text-2xl text-left mb-4">This month</h2>
          <div className="flex flex-col justify-center">
            <div className="flex flex-row justify-around items-center text-left w-full">
              <div>
                <h3 className="text-gray-500">INCOME</h3>
                <span className="font-medium text-2xl">+${incomeBalance}</span>
              </div>
              <div>
                <h3 className="text-gray-500">DEBT</h3>
                <span className="font-medium text-2xl">-${debtBalance}</span>
              </div>
            </div>
            <div className="flex justify-center">
              <CurrentMonthChart />
            </div>
          </div>
        </div>

        <div className="LatestTransactions mt-10 mr-6">
          <h2 className="font-bold text-2xl text-left mb-4">
            Latest Transactions
          </h2>
          <input type="text"/>
        </div>
      </main>
    </div>
  );
}

export default App;
