import React, { Component, useState } from "react";
import Chart from "react-apexcharts";

function CurrentMonthChart() {
  const [state, setState] = useState({
    options: {
      chart: {
        toolbar: { show: false },
        id: "line",
      },
      xaxis: {
        categories: Array.from({ length: 30 }, (_, i) => i + 1), //1 to 30
        labels: { show: true },
      },
    },
    series: [
      {
        name: "series-1",
        data: [
          "9.02",
          "4.95",
          "2.54",
          "3.82",
          "8.60",
          "4.69",
          "2.47",
          "6.19",
          "7.35",
          "7.62",
          "1.04",
          "3.90",
          "5.06",
          "5.38",
          "5.05",
          "3.42",
          "2.12",
          "9.23",
          "6.80",
          "4.43",
          "8.44",
          "1.48",
          "4.00",
          "6.81",
          "1.22",
          "5.95",
          "4.31",
          "8.47",
          "7.47",
          "3.30",
        ],
      },
    ],
  });

  return (
    <div className="CurrentMonthChart">
      <Chart
        options={state.options}
        series={state.series}
        type="line"
        width="370"
      />
    </div>
  );
}

export default CurrentMonthChart;
